INSERT INTO recipe (title, description, time, servings, userID, instructions, dietType) VALUES 
('Vegetarian Pasta Primavera', 'A delightful pasta dish with fresh vegetables and a creamy sauce.', 30, 4, 1, 'Boil pasta according to package instructions. In a separate pan, sauté bell peppers, zucchini, and cherry tomatoes. Mix cooked pasta with sautéed vegetables. Add a creamy Alfredo sauce and stir well. Garnish with fresh basil and Parmesan cheese before serving.', 'Vegetarian'),

('Vegan Buddha Bowl', 'A nutritious and colorful bowl featuring quinoa, roasted vegetables, and avocado.', 45, 2, 1, 'Cook quinoa as per instructions. Roast sweet potatoes, broccoli, and chickpeas in the oven. Assemble the bowl with cooked quinoa, roasted vegetables, sliced avocado, and a tahini dressing.', 'Vegan'),

('Paleo Grilled Salmon', 'Grilled salmon fillets seasoned with herbs and served with steamed vegetables.', 20, 2, 1, 'Season salmon fillets with salt, pepper, and dill. Grill until cooked through. Steam broccoli, carrots, and cauliflower. Serve grilled salmon with steamed vegetables.', 'Paleo'),

('Vegetarian Spinach and Feta Pie', 'A savory pie made with spinach, feta cheese, and flaky pastry.', 35, 3, 1, 'Sauté spinach with garlic until wilted. Mix spinach with crumbled feta cheese. Layer filo pastry sheets in a baking dish, alternating with spinach and feta mixture. Bake until golden brown and crispy.', 'Vegetarian'),

('Vegan Coconut Curry', 'A flavorful curry made with mixed vegetables and coconut milk.', 50, 5, 1, 'Sauté onions, garlic, and curry paste in a pan. Add mixed vegetables and cook until tender. Pour in coconut milk and let it simmer until the sauce thickens. Serve the curry over cooked rice or noodles.', 'Vegan'),

('Paleo Grilled Chicken Salad', 'Grilled chicken breast served on a bed of mixed greens with assorted vegetables.', 40, 2, 1, 'Season chicken breast with salt, pepper, and your favorite herbs. Grill until cooked through. Prepare a salad with mixed greens, cherry tomatoes, cucumber, and avocado slices. Slice grilled chicken and place on top of the salad. Drizzle with balsamic vinaigrette.', 'Paleo'),

('Vegetarian Caprese Sandwich', 'A classic Italian sandwich with fresh mozzarella, tomatoes, basil, and balsamic glaze.', 55, 8, 1, 'Slice fresh mozzarella cheese and tomatoes. Arrange mozzarella, tomatoes, and basil leaves on a baguette. Drizzle with balsamic glaze and season with salt and pepper. Grill the sandwich until the cheese melts and the bread is toasted.', 'Vegetarian'),

('Vegan Lentil Soup', 'Hearty soup made with lentils, vegetables, and aromatic spices.', 25, 1, 1, 'Sauté onions, carrots, and celery in a pot until soft. Add lentils, vegetable broth, diced tomatoes, and spices. Let the soup simmer until lentils are tender. Blend a portion of the soup for a thicker consistency. Serve hot.', 'Vegan'),

('Paleo Grilled Shrimp Skewers', 'Juicy shrimp skewers marinated in lemon and herbs, grilled to perfection.', 70, 10, 1, 'Marinate shrimp in a mixture of lemon juice, garlic, olive oil, and herbs. Thread shrimp onto skewers and grill until opaque and slightly charred. Serve hot with a side of grilled vegetables.', 'Paleo'),

('Vegetarian Stuffed Bell Peppers', 'Bell peppers filled with a flavorful mixture of rice, black beans, corn, and spices.', 20, 2, 1, 'Cook rice according to package instructions. In a bowl, mix cooked rice, black beans, corn, diced tomatoes, and spices. Cut the tops off bell peppers and remove seeds. Stuff peppers with the rice mixture. Bake until peppers are tender. Garnish with cilantro and serve.', 'Vegetarian');
